"use strict";
var chai_1 = require('chai');
var QuillEditor_1 = require('../quill/QuillEditor');
describe('QuillEditor tests', function () {
    it('should return the inner HTML without spaces and new lines', function (done) {
        var innerHTML = '<p>&nbsp;</p> \n <ol>\n <li>sdfsdf < /li> \n < li > sdfsdf < /li>\n< li > sdfsdf < /li>'
            + '\n< /ol>\n <p>&nbsp;</p>\n<p>&nbsp;</p>';
        var expectedValue = '<p>&nbsp;</p><ol><li>sdfsdf < /li>< li > sdfsdf < /li>< li > sdfsdf < /li>< /ol><p>&nbsp;</p><p>&nbsp;</p>';
        chai_1.assert.deepEqual(QuillEditor_1.default.stripOutNewLinesAndSpacesFromInnerHTML(innerHTML), expectedValue, 'html should be the same');
        innerHTML = '<html>\n<body>          \n<div> hello this is my content       </div>\n</body>   \n</html>';
        expectedValue = '<html><body><div> hello this is my content       </div></body></html>';
        chai_1.assert.deepEqual(QuillEditor_1.default.stripOutNewLinesAndSpacesFromInnerHTML(innerHTML), expectedValue, 'html should be the same');
        done();
    });
});
