"use strict";
var chai_1 = require('chai');
var SPPermission_1 = require('../SPPermission');
describe('SPPermission tests', function () {
    it('Fails to create Permission Objects with an invalid value', function () {
        chai_1.assert.throws(function () { new SPPermission_1.default(undefined); });
        chai_1.assert.throws(function () { new SPPermission_1.default(null); });
    });
    it('Fails to find permissions that are not present', function () {
        chai_1.assert.isFalse(SPPermission_1.default.emptyMask.hasAnyPermissions(SPPermission_1.default.addAndCustomizePages, SPPermission_1.default.createSSCSite, SPPermission_1.default.manageSubwebs));
        chai_1.assert.isFalse(SPPermission_1.default.emptyMask.hasAllPermissions(SPPermission_1.default.addAndCustomizePages, SPPermission_1.default.createSSCSite, SPPermission_1.default.manageSubwebs));
    });
    it('Find permissions that are present', function () {
        chai_1.assert.isTrue(SPPermission_1.default.fullMask.hasAnyPermissions(SPPermission_1.default.addAndCustomizePages, SPPermission_1.default.createSSCSite, SPPermission_1.default.manageSubwebs));
        chai_1.assert.isTrue(SPPermission_1.default.fullMask.hasAllPermissions(SPPermission_1.default.addAndCustomizePages, SPPermission_1.default.createSSCSite, SPPermission_1.default.manageSubwebs));
    });
});
